import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './ForgotPassword.css';

const ForgotPassword = () => {
  const [email, setEmail] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(`Verification link sent to: ${email}`);
  };

  return (
    <div style={{
      maxWidth: '400px',
      margin: 'auto',
      padding: '20px',
      textAlign: 'center',
      border: '1px solid #ccc',
      marginTop: '250px',
      borderRadius: '20px',
      boxShadow: '0px 4px 15px rgba(128, 128, 128, 0.5)'
    }}>
      <h2>Recover Account</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '15px' }}>
          <label htmlFor="email" style={{ display: 'block', marginBottom: '5px' }}>Enter your registered email</label>
          <input
            placeholder='Enter your registered email'
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={{ width: '80%', padding: '8px', boxSizing: 'border-box' }}
            required
          />
        </div>
        <button type="submit" style={{ padding: '10px 20px', cursor: 'pointer' }}>
          Send Verification Link
        </button>
      </form>
      <div style={{ marginTop: '20px' }}>
        <Link to="/login">Jump to Login</Link>
      </div>
    </div>
  );
};

export default ForgotPassword;
