import React, { useState } from 'react';

const Registration = () => {
  const registrations = [
    { name: 'Satyam Vatsa', email: '', event: '', phone: '', mode: 'Onsite' },
    { name: 'Khushal Hirani', email: '', event: '', phone: '', mode: 'Onsite' },
    { name: 'Harsh Bachchani', email: '', event: '', phone: '', mode: 'Online' },
    { name: 'Ahmad butt', email: '', event: '', phone: '', mode: 'Uploaded' },
  ];

  // Inline CSS for the App
  const styles = {
    container: {
      display: 'flex',
      height: '100vh',
      fontFamily: 'Arial, sans-serif',
      backgroundColor: '#f5f5f5',
    },
    sidebar: {
      width: '250px',
      backgroundColor: '#ffffff',
      borderRight: '1px solid #e0e0e0',
      padding: '20px',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between',
      boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
    },
    userProfile: {
      marginBottom: '30px',
      textAlign: 'center',
    },
    userAvatar: {
      fontSize: '30px',
      color: '#000',
      fontWeight: 'bold',
      fontSize: '16px',
    },
    navList: {
      listStyleType: 'none',
      padding: 0,
      marginBottom:'200px',
      
    },
    navItem: {
      padding: '10px 15px',
      cursor: 'pointer',
      borderRadius: '5px',
      fontSize: '16px',
      color: '#555',
      transition: 'background-color 0.3s',
      
    

      
    },
    navItemHover: {
      backgroundColor: '#e0e0e0', // Hover effect color
    },
    footer: {
      textAlign: 'center',
      fontSize: '12px',
      color: '#777',
    },
    main: {
      flexGrow: 1,
      display: 'flex',
      flexDirection: 'column',
      backgroundColor: '#fff',
    },
    header: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: '20px',
      backgroundColor: '#fff',
      borderBottom: '1px solid #e0e0e0',
      boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
    },
    dashboardTitle: {
      fontSize: '20px',
      fontWeight: '600',
      color: '#333',
    },
    headerIcons: {
      display: 'flex',
      alignItems: 'center',
    },
    searchInput: {
      padding: '8px 12px',
      marginRight: '10px',
      borderRadius: '20px',
      border: '1px solid #ccc',
    },
    notificationIcon: {
      fontSize: '20px',
      marginLeft: '15px',
      cursor: 'pointer',
    },
    content: {
      flexGrow: 1,
      padding: '20px',
      marginBottom:'30px',
    },
    registrationTable: {
      width: '100%',
      borderCollapse: 'collapse',
      marginTop: '50px',
      fontSize: '14px',
    },
    tableHeader: {
      backgroundColor: '#f8f8f8',
    },
    tableCell: {
      padding: '12px 15px',
      textAlign: 'left',
      borderBottom: '1px solid #e0e0e0',
    },
    tableRow: {
      backgroundColor: '#fff',
      transition: 'background-color 0.3s',
    },
    tableRowHover: {
      backgroundColor: '#f0f0f0',
    },
    checkbox: {
      transform: 'scale(1.2)',
    },
    selectMode: {
      padding: '6px 10px',
      borderRadius: '5px',
      border: '1px solid #ccc',
      fontSize: '14px',
    },
  };

  // Hover effect on navigation items
  const handleNavHover = (e, hover) => {
    if (hover) {
      e.target.style.backgroundColor = '#e0e0e0'; // Grey background on hover
    } else {
      e.target.style.backgroundColor = ''; // Reset background
    }
  };

  return (
    <div style={styles.container}>
      <Sidebar styles={styles} handleNavHover={handleNavHover} />
      <main style={styles.main}>
        <Header styles={styles} />
        <div style={styles.content}>
          <RegistrationTable registrations={registrations} styles={styles} />
        </div>
      </main>
    </div>
  );
};

const Sidebar = ({ styles, handleNavHover }) => (
  <div style={styles.sidebar}>
    <div style={styles.userProfile}>
      <div style={styles.userAvatar}>👤Super Admin</div>
      
    </div>
    <nav>
      <ul style={styles.navList}>
        <li
          style={styles.navItem}
          onMouseEnter={(e) => handleNavHover(e, true)}
          onMouseLeave={(e) => handleNavHover(e, false)}
        >
          Overview
        </li>
        <li
          style={styles.navItem}
          onMouseEnter={(e) => handleNavHover(e, true)}
          onMouseLeave={(e) => handleNavHover(e, false)}
        >
          Event Organizers
        </li>
        <li
          style={styles.navItem}
          onMouseEnter={(e) => handleNavHover(e, true)}
          onMouseLeave={(e) => handleNavHover(e, false)}
        >
          Event Status
        </li>
        <li
          style={{ ...styles.navItem, ...styles.activeNavItem }}
          onMouseEnter={(e) => handleNavHover(e, true)}
          onMouseLeave={(e) => handleNavHover(e, false)}
        >
          Registrations
        </li>
        <li
          style={styles.navItem}
          onMouseEnter={(e) => handleNavHover(e, true)}
          onMouseLeave={(e) => handleNavHover(e, false)}
        >
          Forms
        </li>
      </ul>
    </nav>
    <footer style={styles.footer}>
      <div>Powered by Anginat Events</div>
    </footer>
  </div>
);

const Header = ({ styles }) => (
  <div style={styles.header}>
    <div style={styles.dashboardTitle}>
      Dashboard
    </div>
    <div style={styles.headerIcons}>
      <input type="text" placeholder="Search" style={styles.searchInput} />
      <span style={styles.notificationIcon}>🔔</span>
    </div>
  </div>
);

const RegistrationTable = ({ registrations, styles }) => {
  const [modeSelections, setModeSelections] = useState(
    registrations.map((reg) => reg.mode)
  );

  const handleModeChange = (index, newMode) => {
    const updatedModes = [...modeSelections];
    updatedModes[index] = newMode;
    setModeSelections(updatedModes);
  };

  return (
    <div class="registrations">
      <h3>Registration</h3>
      <table style={styles.registrationTable}>
        <thead>
          <tr>
            <th style={styles.tableCell}></th>
            <th style={styles.tableCell}>Name</th>
            <th style={styles.tableCell}>Email</th>
            <th style={styles.tableCell}>Event</th>
            <th style={styles.tableCell}>Phone No.</th>
            <th style={styles.tableCell}>Mode</th>
          </tr>
        </thead>
        <tbody>
          {registrations.map((registration, index) => (
            <tr
              key={index}
              style={styles.tableRow}
              onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = '#f0f0f0')}
              onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = '#fff')}
            >
              <td style={styles.tableCell}>
                <input type="checkbox" style={styles.checkbox} />
              </td>
              <td style={styles.tableCell}>{registration.name}</td>
              <td style={styles.tableCell}>{registration.email}</td>
              <td style={styles.tableCell}>{registration.event}</td>
              <td style={styles.tableCell}>{registration.phone}</td>
              <td style={styles.tableCell}>
                <select
                  value={modeSelections[index]}
                  onChange={(e) => handleModeChange(index, e.target.value)}
                  style={styles.selectMode}
                >
                  <option value="Onsite">Onsite</option>
                  <option value="Online">Online</option>
                  <option value="Uploaded">Uploaded</option>
                </select>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Registration;
