import React, { useState } from 'react';
import './CreateEvent.css';

const CreateEvent = () => {
  const [eventName, setEventName] = useState('');
  const [eventAddress, setEventAddress] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [attendeeTypes, setAttendeeTypes] = useState({
    Audience: false,
    Delegate: false,
    Speaker: false,
    NRI: false,
  });

  const handleCheckboxChange = (type) => {
    setAttendeeTypes((prev) => ({ ...prev, [type]: !prev[type] }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    console.log({
      eventName,
      eventAddress,
      startDate,
      endDate,
      attendeeTypes,
    });
  };

  return (
    <div className="create-event-form">
      <h2>Create new event - Events</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Event Name</label>
          <input
            type="text"
            value={eventName}
            onChange={(e) => setEventName(e.target.value)}
            placeholder="Full Name"
          />
        </div>
        <div>
          <label>Event Venue Address</label>
          <input
            type="text"
            value={eventAddress}
            onChange={(e) => setEventAddress(e.target.value)}
            placeholder="Address"
          />
        </div>
        <div>
          <label>Event Date</label>
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
          />
          <input
            type="date"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
          />
        </div>
        <div>
          <label>Attendee Types</label>
          <div>
            {Object.keys(attendeeTypes).map((type) => (
              <label key={type}>
                <input
                  type="checkbox"
                  checked={attendeeTypes[type]}
                  onChange={() => handleCheckboxChange(type)}
                />
                {type}
              </label>
            ))}
          </div>
        </div>
        <button type="submit">Next</button>
      </form>
    </div>
  );
};

export default CreateEvent;
