import React, { useState } from 'react';
import './CreateEvent.css';

const CreateEvent = () => {
  const [eventName, setEventName] = useState('');
  const [eventAddress, setEventAddress] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [attendeeTypes, setAttendeeTypes] = useState({
    Audience: false,
    Delegate: false,
    Speaker: false,
    NRI: false,
  });

  const handleCheckboxChange = (type) => {
    setAttendeeTypes((prev) => ({ ...prev, [type]: !prev[type] }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log({
      eventName,
      eventAddress,
      startDate,
      endDate,
      attendeeTypes,
    });
  };

  return (
    <div className="create-event-form">
      <h2>Create new event - Events</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Event Name</label>
          <input
            type="text"
            value={eventName}
            onChange={(e) => setEventName(e.target.value)}
            placeholder="Full Name"
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label>Event Venue Address</label>
          <input
            type="text"
            value={eventAddress}
            onChange={(e) => setEventAddress(e.target.value)}
            placeholder="Address"
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label>Event Date</label>
          <div className="date-inputs">
            <input
              type="date"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
              className="form-control"
            />
            <input
              type="date"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
              className="form-control"
            />
          </div>
        </div>
        <div className="form-group">
          <label>Attendee Types</label>
          <div className="checkbox-group">
            {Object.keys(attendeeTypes).map((type) => (
              <label key={type}>
                <input
                  type="checkbox"
                  checked={attendeeTypes[type]}
                  onChange={() => handleCheckboxChange(type)}
                  className="form-check-input"
                />
                {type}
              </label>
            ))}
          </div>
        </div>
        <button type="submit" className="btn-submit">
          Next
        </button>
      </form>
    </div>
  );
};

export default CreateEvent;
