import React, { useState } from 'react';
import './User1.css'; // Make sure to create a corresponding CSS file

const Dashboard = () => {
  return (
    <div className="dashboard">
      <Sidebar />
      <MainContent />
    </div>
  );
};

const Sidebar = () => {
  return (
    <div className="sidebar">
      <ul>
        <li><i className="fas fa-home"></i> Overview</li>
        <li><i className="fas fa-calendar-alt"></i> Events</li>
        <li><i className="fas fa-file-alt"></i> Reports</li>
        <li><i className="fas fa-paint-brush"></i> Designs</li>
        <li><i className="fas fa-utensils"></i> Lunch/Dinner Kit</li>
        <li><i className="fas fa-cog"></i> Accounts</li>
      </ul>
    </div>
  );
};

const MainContent = () => {
  const [userName, setUserName] = useState('');
  const [userPassword, setUserPassword] = useState('');
  const [permissions, setPermissions] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    // Add logic for submitting the form
    console.log('User Name:', userName);
    console.log('User Password:', userPassword);
    console.log('Permissions:', permissions);
  };

  return (
    <div className="main-content">
      <h2>Users</h2>
      <button className="create-user-btn">Create new User</button>
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <i className="fas fa-user"></i>
          <input 
            type="text"
            placeholder="User Name"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
          />
        </div>
        <div className="input-group">
          <i className="fas fa-lock"></i>
          <input 
            type="password"
            placeholder="User Password"
            value={userPassword}
            onChange={(e) => setUserPassword(e.target.value)}
          />
        </div>
        <div className="input-group">
          <i className="fas fa-edit"></i>
          <input 
            type="text"
            placeholder="Permissions"
            value={permissions}
            onChange={(e) => setPermissions(e.target.value)}
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default Dashboard;
